sap.ui.define(["sap/ui/core/mvc/Controller"], function(Controller) {
	"use strict";
	return Controller.extend("zsmk_data1.controller.v_header", {
		/**
		 *@memberOf zsmk_data1.controller.v_header
		 */
		onDetailPress: function() {
			//This code was generated by the layout editor.
			//alert("detail item pressed");
		},
		/**
		 *@memberOf zsmk_data1.controller.v_header
		 */
		onPress: function(evt) {
			//This code was generated by the layout editor.
			var seleSo = evt.getSource().getBindingContext().getProperty("DocNo");
			//alert(seleSo);
			var oRouter = sap.ui.core.UIComponent.getRouterFor(this);
			//oRouter.getTargets().display("target_detail");
			oRouter.navTo("rout_detail", {
				soID: seleSo
			});
		},
		/**
		 *@memberOf zsmk_data1.controller.v_header
		 */
		onChangePress: function(evt) {
			var seleSo = evt.getSource().getBindingContext().getProperty("DocNo");
			var oRouter = sap.ui.core.UIComponent.getRouterFor(this);
			oRouter.getTargets().display("target_detail",{soID:seleSo,fromTarget:"target_header"});
			//This code was generated by the layout editor.
		}
	});
});